// Fun√ß√£o para validar se a imagem carrega corretamente
async function validateAndApplyImage(imageUrl) {
    console.log('üîç [DEBUG] Iniciando validateAndApplyImage com URL:', imageUrl);
    
    return new Promise((resolve) => {
        const img = new Image();
        let isResolved = false;
        
        img.onload = function() {
            if (!isResolved) {
                isResolved = true;
                console.log('‚úÖ [SUCCESS] Imagem validada com sucesso:', imageUrl);
                console.log('üîç [DEBUG] Dimens√µes da imagem:', img.width, 'x', img.height);
                applyImageToElements(imageUrl);
                resolve(true);
            }
        };
        
        img.onerror = function() {
            if (!isResolved) {
                isResolved = true;
                console.error('‚ùå [ERROR] Erro ao carregar imagem:', imageUrl);
                console.log('üîÑ [INFO] Aplicando imagem padr√£o devido ao erro de carregamento');
                applyDefaultImage();
                resolve(false);
            }
        };
        
        // Timeout de 10 segundos para evitar travamento
        setTimeout(() => {
            if (!isResolved) {
                isResolved = true;
                console.warn('‚è∞ [WARNING] Timeout na valida√ß√£o da imagem (10s):', imageUrl);
                console.log('üîÑ [INFO] Aplicando imagem padr√£o devido ao timeout');
                applyDefaultImage();
                resolve(false);
            }
        }, 10000);
        
        console.log('üîÑ [DEBUG] Iniciando carregamento da imagem...');
        img.src = imageUrl;
    });
}

