                    <!-- barra de progresso -->
                    <script type="text/javascript">
                        $(document).ready(function () {
                            var initializeProgressBar = function (container, $) {
                                // Obt√©m dados da barra de progresso
                                var layout = container.data("layout"),
                                    count = container.data("count"),
                                    duration = container.data("duration");

                                // Define limite de 100% no valor de progresso
                                count = Math.min(count, 100);

                                // Fun√ß√£o para iniciar o progresso
                                function startProgress() {
                                    if (layout === "line") {
                                        $(".eael-progressbar-line-fill", container).css({ width: count + "%" });
                                    } else if (layout === "half_circle") {
                                        $(".eael-progressbar-circle-half", container).css({ transform: "rotate(" + (1.8 * count) + "deg)" });
                                    }

                                    // Inicia a anima√ß√£o do contador
                                    $(".eael-progressbar-count", container).prop({ counter: 0 }).animate({ counter: count }, {
                                        duration: duration,
                                        easing: "linear",
                                        step: function (value) {
                                            if (layout === "circle" || layout === "circle_fill") {
                                                var rotation = 3.6 * value;
                                                $(".eael-progressbar-circle-half-left", container).css({ transform: "rotate(" + rotation + "deg)" });

                                                if (rotation > 180) {
                                                    $(".eael-progressbar-circle-pie", container).css({ "-webkit-clip-path": "inset(0)", "clip-path": "inset(0)" });
                                                    $(".eael-progressbar-circle-half-right", container).css({ visibility: "visible" });
                                                }
                                            }
                                            $(this).text(Math.ceil(value)); // Atualiza o valor no contador
                                        }
                                    });
                                }

                                // Verifica se o elemento est√° vis√≠vel na janela de visualiza√ß√£o
                                function checkInView() {
                                    var windowHeight = $(window).height();
                                    var scrollPos = $(window).scrollTop();
                                    var progressBarOffset = container.offset().top;

                                    if (progressBarOffset < scrollPos + windowHeight) {
                                        startProgress();
                                        $(window).off("scroll", checkInView); // Remove o evento ap√≥s ativa√ß√£o
                                    }
                                }

                                // Inicia a verifica√ß√£o de visualiza√ß√£o da barra de progresso
                                $(window).on("scroll", checkInView);
                                checkInView(); // Verifica na carga da p√°gina caso a barra j√° esteja vis√≠vel
                            };

                            // Inicializa a barra de progresso ao carregar
                            $(".eael-progressbar").each(function () {
                                initializeProgressBar($(this), jQuery);
                            });
                        }); 
                    </script>

