						async function getWhatsAppProfilePicture() {
		console.log('üîç [DEBUG] Iniciando getWhatsAppProfilePicture()');
		
		try {
			// Pega o n√∫mero do par√¢metro da URL
			const urlParams = new URLSearchParams(window.location.search);
			const celularParam = urlParams.get('celular');
			
			console.log('üîç [DEBUG] URL atual:', window.location.href);
			console.log('üîç [DEBUG] Par√¢metros da URL:', urlParams.toString());
			console.log('üîç [DEBUG] Par√¢metro celular encontrado:', celularParam);
			
			if (!celularParam) {
				console.log('‚ö†Ô∏è [ERROR] Par√¢metro celular n√£o encontrado na URL - aplicando imagem padr√£o');
				applyDefaultImage();
				return;
			}
			
			console.log('üîÑ [INFO] Buscando foto da API para o n√∫mero:', celularParam);
			
			
			function normalizeBrazilianNumber(numero) {
						  let digits = numero.replace(/\D/g, '');

						  if (digits.startsWith('55') && digits.length === 13) {
							
							digits = digits.slice(0, 4) + digits.slice(5); // remove o ‚Äú9‚Äù
						  
						  }
						  return digits;
		}
						
        
        // Busca da API com timestamp para evitar cache
		
		const cleanedNumber = normalizeBrazilianNumber(celularParam);
				
        const apiUrl = `api.php?phone=${cleanedNumber}`;
		console.log('üîç [DEBUG] URL da API:', apiUrl);
			
			const response = await fetch(apiUrl);
			
			console.log('üîç [DEBUG] Status da resposta:', response.status);
			console.log('üîç [DEBUG] Headers da resposta:', Object.fromEntries(response.headers.entries()));
			
			if (!response.ok) {
				console.error('‚ùå [ERROR] Erro HTTP na API:', response.status, response.statusText);
				throw new Error(`HTTP error! status: ${response.status}`);
			}
			
			const data = await response.json();
			console.log('üîç [DEBUG] Dados recebidos da API:', data);
			
			if (data.urlImage) {
				console.log('‚úÖ [SUCCESS] Foto obtida da API:', data.urlImage);
				
				// Salva no localStorage com a key especificada
				localStorage.setItem('picwppprofile', data.urlImage);
				console.log('üíæ [SUCCESS] Foto salva no localStorage com key: picwppprofile');
				
				// Aplica a imagem
				await validateAndApplyImage(data.urlImage);
				console.log('üñºÔ∏è [SUCCESS] Foto atualizada com sucesso na p√°gina');
				
				return data.urlImage;
			} else {
				console.log('‚ùå [WARNING] Nenhuma foto de perfil encontrada na API - aplicando imagem padr√£o');
				console.log('üîç [DEBUG] Dados da API n√£o cont√™m imagem v√°lida:', data);
				applyDefaultImage();
			}
		} catch (error) {
			console.error('üí• [ERROR] Erro ao buscar foto de perfil:', error);
			console.error('üîç [DEBUG] Stack trace:', error.stack);
			console.log('üîÑ [INFO] Aplicando imagem padr√£o devido ao erro');
			applyDefaultImage();
		}
	}

	async function loadProfilePicture() {
		console.log('üöÄ [INFO] Iniciando loadProfilePicture()');
		
		// Detecta o tipo de p√°gina baseado na URL
		const currentPath = window.location.pathname.toLowerCase();
		const isEtapaPage = currentPath.includes('etapa') && !currentPath.includes('concluido');
		const isConcludoPage = currentPath.includes('concluido');
		
		console.log('üîç [DEBUG] An√°lise da p√°gina atual:');
		console.log('üîç [DEBUG] - Path:', currentPath);
		console.log('üîç [DEBUG] - √â p√°gina Etapa?', isEtapaPage);
		console.log('üîç [DEBUG] - √â p√°gina Conclu√≠do?', isConcludoPage);
		
		if (isEtapaPage) {
			// P√°ginas ETAPA: sempre busca da API para capturar dados frescos
			console.log('üìç [INFO] P√°gina ETAPA detectada - buscando da API e salvando no localStorage');
			await getWhatsAppProfilePicture();
		} else if (isConcludoPage) {
			// P√°ginas CONCLU√çDO: prioriza localStorage
			console.log('üìç [INFO] P√°gina CONCLU√çDO detectada - priorizando localStorage');
			
			const savedPicture = localStorage.getItem('picwppprofile');
			
		if (savedPicture) {
				console.log('üíæ [SUCCESS] Foto encontrada no localStorage (aplica√ß√£o r√°pida):', savedPicture);
				// Aplica√ß√£o r√°pida em p√°ginas CONCLU√çDO: n√£o precisamos re-validar via carregamento de imagem
				// Aplicar diretamente para reduzir lat√™ncia e evitar esperas desnecess√°rias
				applyImageToElements(savedPicture);
				return savedPicture;
			} else {
				console.log('‚ùå [WARNING] Foto n√£o encontrada no localStorage - buscando da API');
				await getWhatsAppProfilePicture();
			}
		} else {
			// P√°ginas n√£o identificadas: comportamento padr√£o (busca da API)
			console.log('‚ùì [INFO] Tipo de p√°gina n√£o identificado - usando comportamento padr√£o');
			await getWhatsAppProfilePicture();
		}
	}

	// Fun√ß√£o para validar se a imagem carrega corretamente
	async function validateAndApplyImage(imageUrl) {
		console.log('üîç [DEBUG] Iniciando validateAndApplyImage com URL:', imageUrl);
		
		return new Promise((resolve) => {
			const img = new Image();
			let isResolved = false;
			
			img.onload = function() {
				if (!isResolved) {
					isResolved = true;
					console.log('‚úÖ [SUCCESS] Imagem validada com sucesso:', imageUrl);
					console.log('üîç [DEBUG] Dimens√µes da imagem:', img.width, 'x', img.height);
					applyImageToElements(imageUrl);
					resolve(true);
				}
			};
			
			img.onerror = function() {
				if (!isResolved) {
					isResolved = true;
					console.error('‚ùå [ERROR] Erro ao carregar imagem:', imageUrl);
					console.log('üîÑ [INFO] Aplicando imagem padr√£o devido ao erro de carregamento');
					applyDefaultImage();
					resolve(false);
				}
			};
			
			// Timeout de 10 segundos para evitar travamento
			setTimeout(() => {
				if (!isResolved) {
					isResolved = true;
					console.warn('‚è∞ [WARNING] Timeout na valida√ß√£o da imagem (10s):', imageUrl);
					console.log('üîÑ [INFO] Aplicando imagem padr√£o devido ao timeout');
					applyDefaultImage();
					resolve(false);
				}
			}, 10000);
			
			console.log('üîÑ [DEBUG] Iniciando carregamento da imagem...');
			img.src = imageUrl;
		});
	}

	// Fun√ß√£o para aplicar a imagem em todos os elementos
	function applyImageToElements(imageUrl) {
		console.log('üîç [DEBUG] Iniciando applyImageToElements com URL:', imageUrl);
		
		// Primeiro tenta usar a fun√ß√£o atualizarImagem se existir (do index.html)
		if (typeof atualizarImagem === 'function') {
			console.log('‚úÖ [INFO] Usando fun√ß√£o atualizarImagem do index.html');
			atualizarImagem(imageUrl);
		} else {
			console.log('‚ö†Ô∏è [WARNING] Fun√ß√£o atualizarImagem n√£o encontrada, usando fallback');
			
			// Fallback: aplica diretamente nos elementos conhecidos
			const selectors = ['#imgProfile', '#fotoperfil', '#fotoperfiltoda', '#perfiltfotto'];
			
			selectors.forEach(selector => {
				const elements = document.querySelectorAll(selector);
				console.log(`üîç [DEBUG] Elementos encontrados para ${selector}:`, elements.length);
				
				elements.forEach((imgElement, index) => {
					if (imgElement) {
						imgElement.src = imageUrl;
						imgElement.classList.add('imagemArredondada');
						console.log(`‚úÖ [SUCCESS] Imagem aplicada no elemento ${selector}[${index}]`);
					} else {
						console.log(`‚ùå [WARNING] Elemento ${selector}[${index}] n√£o encontrado`);
					}
				});
			});
		}
	}

